val sRDD7 = dssc44.NetcdfFile("hdfs://localhost:9000/user/root/TRMM3hrly2010.txt", List("precipitation", "nlat", "nlon"), 3)

// parse the date out from the openDap URL
val labeled7 = sRDD7.map(p1 => {
    val filename = p1.metaData("SOURCE").split("/").last.split("\\.")
    val fileDate = (filename(1) + filename(2)).toInt
    p1.insertDictionary(("Date", fileDate.toString))
    p1
})
